import React, { Component } from "react";
import axios from "axios";

class Seats extends Component {
  state = {
    seats: [],
  };

  async componentDidMount() {
    const { data: seats } = await axios.get("http://localhost:3900/api/seats");
    this.setState({ seats });
  }

  render() {
    if (this.state.seats.length === 0)
      return <h2>There are currently no seat available</h2>;
    return (
      <React.Fragment>
        <h2>There are currently {this.state.seats.length} seats available</h2>
        <table className="table">
          <thead>
            <tr>
              <th>Seat's Number</th>
              <th>Type of Seat</th>
              <th>Cost</th>
              <th></th>
            </tr>
          </thead>
          <tbody>
            {this.state.seats.map((seat) => (
              <tr key={seat._id}>
                <td>{seat.seat_no}</td>
                <td>{seat.type}</td>
                <td>{seat.cost}</td>

                <td>
                  <button
                    onClick={this.makeAppointment}
                    className="btn btn-warning btn-sm"
                  >
                    Request for Booking Seat
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </React.Fragment>
    );
  }
}

export default Seats;
