import React, { Component } from "react";
import axios from "axios";
import SearchBox from "./common/searchBox";

class Seats extends Component {
  state = {
    seats: [],
    searchQuery: "",
  };

  async componentDidMount() {
    const { data: seats } = await axios.get("http://localhost:3900/api/seats");
    this.setState({ seats });
  }

  getSeats() {
    const allSeats = this.state.seats;
    let filteredSeats = allSeats;

    if (this.state.searchQuery) {
      filteredSeats = allSeats.filter((d) =>
        d.type.toLowerCase().startsWith(this.state.searchQuery.toLowerCase())
      );
    }

    return filteredSeats;
  }

  handleSearch = (query) => {
    this.setState({ searchQuery: query });
  };

  render() {
    if (this.state.seats.length === 0)
      return <h4 className="text">There are currently no seat available</h4>;

    const ambulances = this.getAmbulances();
    return (
      <React.Fragment>
        <h4 className="text">
          There are currently {this.state.seats.length} seats available
        </h4>
        <table className="table">
          <thead>
            <tr>
              <th>Seat's Number</th>
              <th>Type of Seat</th>
              <th>Cost</th>
              <th></th>
            </tr>
          </thead>
          <tbody>
            {this.state.seats.map((seat) => (
              <tr key={seat._id}>
                <td>{seat.seat_no}</td>
                <td>{seat.type}</td>
                <td>{seat.cost}</td>

                <td>
                  <button
                    onClick={this.makeAppointment}
                    className="btn btn-warning btn-sm"
                  >
                    Request for Booking Seat
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </React.Fragment>
    );
  }
}

export default Seats;
