import React, { Component } from "react";
import Form from "./form";
import Joi, { errors } from "joi-browser";

class RegisterForm extends Form {
  state = {
    data: {
      username: "",
      password: "",
      name: "",
      phone: "",
      blood_group: "",
      address: "",
    },
    errors: {},
  };

  schema = {
    username: Joi.string().email().min(5).max(255).required().label("Email"),
    password: Joi.string().min(5).max(255).required().label("Password"),
    name: Joi.string().min(3).max(255).required().label("Name"),
    phone: Joi.string().min(8).max(15).label("Phone"),
    blood_group: Joi.string()
      .min(2)
      .valid("A+", "B+", "AB+", "O+", "A-", "B-", "AB-", "O-")
      .label("Blood Group"),
    address: Joi.string().label("Address"),
  };

  doSubmit = () => {
    //call server
    console.log("submiited");
  };

  render() {
    return (
      <div>
        <h1>Register Here</h1>
        <form onSubmit={this.handleSubmit}>
          {this.renderInput("name", "Name")}
          {this.renderInput("username", "Email")}
          {this.renderInput("password", "Password", "password")}
          {this.renderInput("phone", "Phone")}
          {this.renderInput("blood_group", "Blood Group")}
          {this.renderInput("address", "Address")}
          {this.renderButton("Register")}
        </form>
      </div>
    );
  }
}

export default RegisterForm;
