import React, { Component } from "react";
import axios from "axios";

class DoctorProfile extends Component {
  state = {
    doctor: [],
  };

  async componentDidMount() {
    const { data: doctor } = await axios.get(
      `http://localhost:3900/api/doctors/${this.props.match.params.id}`
    );
    this.setState({ doctor });
  }
  render() {
    return (
      <table className="table">
        <tr>
          <th>Doctor's Name</th>
          <td>{this.state.doctor[0].name}</td>
        </tr>
        <tr>
          <th>Doctor's Email</th>
          <td>{this.state.doctor[0].email}</td>
        </tr>
        <tr>
          <th>Sitting Time</th>
          <td>{this.state.doctor[0].sitting_time}</td>
        </tr>
        <tr>
          <th>Qualifications</th>
          <td>{this.state.doctor[0].qualifications}</td>
        </tr>
        <tr>
          <th>Availability</th>
          <td>{this.state.doctor[0].availability}</td>
        </tr>
      </table>
    );
  }
}

export default DoctorProfile;
