import React, { Component, useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

export default function DoctorProfile() {
  let params = useParams();
  let [doctor, setDoctor] = useState(null);

  useEffect(() => {
    if (doctor && doctor.id === params.id) {
      return;
    }
    getDoctorDetails();
  });

  function getDoctorDetails() {
    axios
      .get("http://localhost:3900/api/doctors/" + params.id)
      .then((response) => {
        setDoctor({ ...response.data, id: params.id });
      });
  }

  if (doctor) {
    return (
      <React.Fragment>
        <table className="table">
          <tr>
            <th>Doctor's ID-</th>
            <td>{doctor.id}</td>
          </tr>
          <tr>
            <th>Doctor's Name-</th>
            <td>{doctor.name}</td>
          </tr>
          <tr>
            <th>Doctor's Email-</th>
            <td>{doctor.email}</td>
          </tr>
          <tr>
            <th>Sitting Time-</th>
            <td>{doctor.sitting_time}</td>
          </tr>
          <tr>
            <th>Qualifications-</th>
            <td>{doctor.qualifications}</td>
          </tr>
          <tr>
            <th>Availability-</th>
            <td>{doctor.availability}</td>
          </tr>
        </table>
        <h1>Doctor's ID: {doctor.id}</h1>
        <h2>Doctor's Name: {doctor.name}</h2>
        <h2>Doctor's Email: {doctor.email}</h2>
        <h3>Sitting Time: {doctor.sitting_time}</h3>
        <h3>Qualifications: {doctor.qualifications}</h3>
      </React.Fragment>
    );
  }

  return null;
}
