import React, { Component } from "react";
import axios from "axios";

class Medicines extends Component {
  state = {
    medicines: [],
  };

  async componentDidMount() {
    const { data: medicines } = await axios.get(
      "http://localhost:3900/api/medicines"
    );
    this.setState({ medicines });
  }

  render() {
    if (this.state.medicines.length === 0)
      return <p>There are currently no medicine available</p>;
    return (
      <React.Fragment>
        <p>
          There are currently {this.state.medicines.length} medicines available
        </p>
        <table className="table">
          <thead>
            <tr>
              <th>Medicine Name</th>
              <th>Price</th>
              <th>Stock</th>
              <th></th>
            </tr>
          </thead>
          <tbody>
            {this.state.medicines.map((medicine) => (
              <tr key={medicine._id}>
                <td>{medicine.name}</td>
                <td>{medicine.price}</td>
                <td>{medicine.stock}</td>

                <td>
                  <button
                    onClick={this.makeAppointment}
                    className="btn btn-warning btn-sm"
                  >
                    Order for Medicine
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </React.Fragment>
    );
  }
}

export default Medicines;
