import React, { Component } from "react";
import axios from "axios";
import { Route, Routes, Link, Outlet } from "react-router-dom";

class Doctors extends Component {
  state = {
    doctors: [],
  };

  async componentDidMount() {
    const { data: doctors } = await axios.get(
      "http://localhost:3900/api/doctors"
    );
    this.setState({ doctors });
  }

  render() {
    if (this.state.doctors.length === 0)
      return <p>There are currently no doctor available to make appointment</p>;
    return (
      <React.Fragment>
        <p>
          There are {this.state.doctors.length} doctor/s available to make
          appointment
        </p>
        <table className="table">
          <thead>
            <tr>
              <th>Doctor's Name</th>
              <th>Email</th>
              <th>Sitting Time</th>
              <th>Qualifications</th>
              <th></th>
            </tr>
          </thead>
          <tbody>
            {this.state.doctors.map((doctor) => (
              <tr key={doctor._id}>
                <td>
                  <Link to={`/doctors/${doctor._id}`}>{doctor.name}</Link>
                </td>
                <td>{doctor.email}</td>
                <td>{doctor.sitting_time}</td>
                <td>{doctor.qualifications}</td>
                <td>
                  <button
                    onClick={this.makeAppointment}
                    className="btn btn-warning btn-sm"
                  >
                    Make Appointment
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
        <Outlet />
      </React.Fragment>
    );
  }
}

export default Doctors;
