import React, { Component } from "react";
import axios from "axios";

const DoctorProfile = async ({ match }) => {
  let doctor = {};
  const { data } = await axios.get(
    `http://localhost:3900/api/doctors/${this.props.match.params.id}`
  );
  doctor = data[0];
  console.log(doctor);
  return (
    <React.Fragment>
      <p>Doctor's Name: doctor.name</p>
    </React.Fragment>
  );
};

export default DoctorProfile;
