import React, { Component } from "react";
import Form from "../common/form";
import Joi, { errors } from "joi-browser";
import axios from "axios";
import { toast } from "react-toastify";
import { useNavigate } from "react-router-dom";

class AppointmentForm extends Form {
  state = {
    data: {
      userId: localStorage.getItem("token"),
      doctorId: this.props.params.id,
      patient_name: "",
      patient_sex: "",
      patient_age: "",
      appointment_date: "",
    },
    errors: {},
  };

  schema = {
    userId: Joi.objectid().required(),
    doctorId: Joi.objectid().required(),
    patient_name: Joi.string().min(3).max(255).required(),
    patient_sex: Joi.string().valid("Male", "Female"),
    patient_age: Joi.number().min(0).max(200),
    appointment_date: Joi.date().required(),
  };

  doSubmit = async () => {
    //call server
    const obj = this.state.data;
    try {
      const response = await axios.post(
        "http://localhost:3900/api/appointments",
        obj
      );
      console.log(response);
      toast.success("successfully submitted:-)");
      this.props.history("/");
    } catch (ex) {
      if (ex.response && ex.response.status === 400) {
        const errors = { ...this.state.errors };
        this.setState({ errors });
        toast.error("failed to make appointment!");
      }
    }
  };

  render() {
    return (
      <div>
        <h1>Make Appointment</h1>
        <form onSubmit={this.handleSubmit}>
          {this.renderInput("patient_name", "Patient's Name")}
          {this.renderInput("patient_sex", "Sex")}
          {this.renderInput("patient_age", "Age")}
          {this.renderInput("appointment_date", "Appointment Date")}
          {this.renderButton("Register")}
        </form>
      </div>
    );
  }
}

export default (props) => <AppointmentForm history={useNavigate()} />;
